$("#datepicker").datepicker({
    dateFormat: "dd/mm/yy", // Ajusta el formato de la fecha a tu preferencia
    minDate: new Date(), // Opcional: Establece la fecha mínima al día actual
    maxDate: "+1Y" // Opcional: Establece la fecha máxima a un año a partir de hoy
});

$("#timepicker").timepicker({
    timeFormat: 'hh:mm p', // Formato de 12 horas con AM/PM
    interval: 60, // Intervalo de tiempo en minutos
    minTime: '9', // Hora mínima disponible
    maxTime: '22:00', // Hora máxima disponible
    startTime: '09:00', // Hora de inicio del timepicker
    dynamic: false, // Asegúrate de que la lista no se reajuste al seleccionar
    dropdown: true, // Permite que se despliegue la lista de tiempos
    scrollbar: true // Permite desplazarse por la lista si es necesario
});
